//! `CodeBlock` component for displaying code

use dioxus::prelude::*;

stylance::import_style!(style, "code_block.module.scss");

/// Supported languages for code blocks (display purposes only, no syntax highlighting)
#[derive(Debug, Clone, Copy, PartialEq, Eq)]
pub enum Language {
    /// Rust programming language
    Rust,
    /// Assembly language
    Asm,
    /// SCSS (Sassy CSS)
    Scss,
    /// JavaScript
    Javascript,
    /// TypeScript (also used for TSX)
    TypeScript,
    /// HTML markup
    Html,
    /// CSS stylesheets
    Css,
    /// JSON data format
    Json,
    /// TOML configuration format
    Toml,
    /// YAML data format
    Yaml,
    /// Markdown text format
    Markdown,
    /// Python programming language
    Python,
    /// Bash/shell scripts
    Bash,
    /// Go programming language
    Go,
}

impl Language {
    /// Get language as string
    #[must_use]
    pub const fn as_str(&self) -> &'static str {
        match self {
            Self::Rust => "rust",
            Self::Asm => "asm",
            Self::Scss => "scss",
            Self::Javascript => "javascript",
            Self::TypeScript => "typescript",
            Self::Html => "html",
            Self::Css => "css",
            Self::Json => "json",
            Self::Toml => "toml",
            Self::Yaml => "yaml",
            Self::Markdown => "markdown",
            Self::Python => "python",
            Self::Bash => "bash",
            Self::Go => "go",
        }
    }
}

