/// Size variants for code blocks
#[derive(Debug, Clone, Copy, Default, PartialEq, Eq)]
pub enum CodeBlockSize {
    /// Compact padding
    Sm,
    /// Default padding
    #[default]
    Md,
    /// Comfortable padding
    Lg,
}

/// Code block component (no syntax highlighting)
#[component]
pub fn CodeBlock(
    /// The source code to display
    code: String,
    /// Programming language (for display purposes only)
    language: Option<Language>,
    /// Optional filename to display (replaces language name if set)
    filename: Option<String>,
    /// Show language label in header (ignored if filename is set)
    #[props(default = true)]
    show_language: bool,
    /// Show line numbers
    #[props(default = false)]
    show_line_numbers: bool,
    /// Show copy button
    #[props(default = true)]
    show_copy: bool,
    /// Size variant
    #[props(default)]
    size: CodeBlockSize,
) -> Element {
    // Needs `mut` for wasm32 (where .set() is called) but not server
    #[allow(unused_mut)]
    let mut copied = use_signal(|| false);

    // Escape HTML (no syntax highlighting)
    let mut escaped = String::new();
    html_escape::encode_text_to_string(&code, &mut escaped);

    let line_count = code.lines().count();

    // Build line numbers HTML if needed
    let line_numbers_html = if show_line_numbers {
        (1..=line_count)
            .map(|n| format!("<span>{n}</span>"))
            .collect::<Vec<_>>()
            .join("\n")
    } else {
        String::new()
    };

    let size_class = match size {
        CodeBlockSize::Sm => style::sm,
        CodeBlockSize::Md => style::md,
        CodeBlockSize::Lg => style::lg,
    };

    let icon_html = language.and_then(language_icon);
    // Use filename if provided, otherwise use language name
    let display_name: Option<String> =
        filename.clone().or_else(|| language.map(|l| l.as_str().to_string()));
    let show_header = (show_language && display_name.is_some()) || filename.is_some() || show_copy;

    rsx! {
        div { class: stylance::classes!(style::container, size_class),
            if show_header {
                div { class: style::header,
                    if let Some(name) = &display_name {
                        if show_language || filename.is_some() {
                            div { class: style::language_wrapper,
                                if let Some(svg) = icon_html {
                                    span {
                                        class: style::language_icon,
                                        dangerous_inner_html: "{svg}",
                                    }
                                }
                                span { class: style::language, "{name}" }
                            }
                        }
                    }
                    if show_copy {
                        button {
                            class: style::copy_button,
                            title: "Copy to clipboard",
                            onclick: {
                                #[cfg(target_arch = "wasm32")]
                                let code = code.clone();
                                move |_| {
                                    #[cfg(target_arch = "wasm32")]
                                    {
                                        let code = code.clone();
                                        spawn(async move {
                                            if let Some(clipboard) = web_sys::window().map(|w| w.navigator().clipboard()) {
                                                let _ = wasm_bindgen_futures::JsFuture::from(clipboard.write_text(&code)).await;
                                                copied.set(true);
                                                gloo_timers::callback::Timeout::new(2000, move || {
                                                    copied.set(false);
                                                })
                                                .forget();
                                            }
                                        });
                                    }
                                }
                            },
                            if copied() {
                                svg {
                                    view_box: "0 0 24 24",
                                    fill: "none",
                                    stroke: "currentColor",
                                    stroke_width: "2",
                                    path { d: "M20 6 9 17l-5-5" }
                                }
                            } else {
                                svg {
                                    view_box: "0 0 24 24",
                                    fill: "none",
                                    stroke: "currentColor",
                                    stroke_width: "2",
                                    rect {
                                        width: "14",
                                        height: "14",
                                        x: "8",
                                        y: "8",
                                        rx: "2",
                                        ry: "2",
                                    }
                                    path { d: "M4 16c-1.1 0-2-.9-2-2V4c0-1.1.9-2 2-2h10c1.1 0 2 .9 2 2" }
                                }
                            }
                        }
                    }
                }
            }
            div { class: style::code_wrapper,
                if show_line_numbers {
                    div {
                        class: style::line_numbers,
                        dangerous_inner_html: "{line_numbers_html}",
                    }
                }
                pre { class: style::pre,
                    code {
                        class: style::code,
                        dangerous_inner_html: "{escaped}",
                    }
                }
            }
        }
    }
}

/// Inline code styling
#[component]
pub fn InlineCode(children: Element) -> Element {
    rsx! {
        code { class: style::inline, {children} }
    }
}
